import {GET_LOGIN_DETAILS, POST_LOGIN, POST_LOGIN_ERROR , GET_USER,UPDATE_STATUS,  GET_ALL_MASTER_DATA, POST_FORGET, POST_FORGET_ERROR , GET_BATCH_VERIFICATION} from './types';


const initialState = {
    login_response:'',
    login_response_err:'',
    message:'waiting for the reducer',
    forget_response:'',
    forget_response_err:'',
    message:'OTP Sent',
    get_batch_verification:'', 
    getallMasters_data:'',
    getMis_report:'',

}



export default function reducer(state=initialState,{type,payload}){
    switch(type){
        case GET_LOGIN_DETAILS:
            return{
                ...state
            }
        case POST_LOGIN:
            return{
                ...state,
                login_response:payload
            }
        case POST_LOGIN_ERROR:
            return{
                ...state,
                login_response_err:payload
            }

        case POST_FORGET:
            return{
                ...state,
                forget_response:payload
            }    
        case POST_FORGET_ERROR:
            return{
                ...state,
                forget_response_err:payload
        }

        case GET_USER:{

            return{
                ...state,
                user: payload
            }
        }
        //get MIS Report for roles


        //igr-batch-verification
        case GET_BATCH_VERIFICATION:
            return{
                ...state 
            }      

        case UPDATE_STATUS:
            return{
                ...state
            }
        default:
            return{
                ...state
            }
    }

}
