import React, { Component } from 'react';
import titleIcon from '../../Assests/icon2/Bimg.svg';
import loginIcon from '../../Assests/icon2/loginIcon.svg';
import captachImg from '../../Assests/icon2/captach.PNG';
import SignIn_OTP from './Otp';
import { loginApi , changePassword } from '../../store/Login/action';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import signin_bannerImg from '../../Assests/icon/signin_bannerImg.png'
import RCG from 'react-captcha-generator';
import { BsJustify } from 'react-icons/bs';
import * as CryptoJS from 'crypto-js'
const MySwal = withReactContent(Swal);



class ChangePass extends Component{

    constructor(props){
        super(props);
        this.state={
            email:'',
            newPassword:'',
            confirmPassword:'',
            role:'',
            Zone:'',
            District:'',
            booleanOTpPage:false,
            captcha:'',
            captchas:'',
          
        }

    }


    //OnChange function for input box 
    handleChange=(e)=>{
        const {name, value} = e.target;
        this.setState({...this.state,[name]:value})
        
    }


    //Submit Button function for change password
    handleChangePassword = () => {
        const encryptconfigs = {
            "key": "t700#zkrF@db0705",
            "iv": "i700#zkrF@db0705"
          }
          //email
          var key = CryptoJS.enc.Latin1.parse(encryptconfigs.key);
              var iv = CryptoJS.enc.Latin1.parse(encryptconfigs.iv);
              var encryptedData = CryptoJS.AES.encrypt(this.props.location.state.email, key,{
                iv: iv,
                mode: CryptoJS.mode.CBC,
                padding: CryptoJS.pad.Pkcs7,
              });
             
              encryptedData = encryptedData.toString()
              console.log(encryptedData)
        //newpassword
        var key = CryptoJS.enc.Latin1.parse(encryptconfigs.key);
              var iv = CryptoJS.enc.Latin1.parse(encryptconfigs.iv);
              var encryptedData1 = CryptoJS.AES.encrypt(this.state.newPassword, key,{
                iv: iv,
                mode: CryptoJS.mode.CBC,
                padding: CryptoJS.pad.Pkcs7,
              });
             
              encryptedData1 = encryptedData1.toString()
              console.log(encryptedData1)
        
        //confirmpassword
        var key = CryptoJS.enc.Latin1.parse(encryptconfigs.key);
        var iv = CryptoJS.enc.Latin1.parse(encryptconfigs.iv);
        var encryptedData2 = CryptoJS.AES.encrypt(this.state.confirmPassword, key,{
          iv: iv,
          mode: CryptoJS.mode.CBC,
          padding: CryptoJS.pad.Pkcs7,
        });
       
        encryptedData2 = encryptedData2.toString()
        console.log(encryptedData2)

        //token
        var key = CryptoJS.enc.Latin1.parse(encryptconfigs.key);
        var iv = CryptoJS.enc.Latin1.parse(encryptconfigs.iv);
        var encryptedData3 = CryptoJS.AES.encrypt(this.props.location.state.token, key,{
          iv: iv,
          mode: CryptoJS.mode.CBC,
          padding: CryptoJS.pad.Pkcs7,
        });
       
        encryptedData3 = encryptedData3.toString()
        console.log(encryptedData3)

        const data={
            email : encryptedData,
            newpassword : encryptedData1,
            confirmpassword : encryptedData2,
            token: encryptedData3,
        }
        console.log(data);
        this.props.changePassword(data);
        MySwal.fire(
           "Password Successfully Changes"
        ).then(() => this.props.history.push('/'))
    }

    componentDidUpdate = (prev) => {
        
    }


    render(){
        console.log(this.state);
        console.log(this.props);
        if(this.props.history.location.state === undefined){
            this.props.history.push('/')
        }
        return (
            <div className='login-layout'>
                <div className='login-sub-layout'>
                    <div className='row mt-3'>
                        <div className='col-md-8  login-titleRow'>
                            <img src={titleIcon} /> &nbsp;&nbsp;
                            <label>Blockchained Documents Of Registration</label>
                            <div className=' text'>
                                All Tamil Nadu Registration documents secured by Tamil Nadu Blockchain backbone infrastructure.
                            </div>
                            <div className='login-bannerImg'>
                                <img src={signin_bannerImg} /> &nbsp;&nbsp;    
                            </div>
                        </div >
                        <div className='col-md-4'>
                            <div className='login-card'>
                                <div className='login-card-sub-layout'>
                                    <div className='row'>
                                        <div className='col-md-12 loginRow1'>
                                            <img src={loginIcon} /> &nbsp;&nbsp; <label>Change the Password</label>
                                        </div>
                                    </div>
                                    <div className='row'>
                                        <div className='col-md-12'>
                                            <label>New Password</label>
                                        </div>
                                        <div className='col-md-12 mt-2'>
                                            <input type="password" name="newPassword" onChange={this.handleChange} value={this.state.newPassword}  placeholder='new Password' required/>
                                        </div>
                                    </div>
                                    <div className='row mt-4'>
                                        <div className='col-md-12'>
                                            <label>Confirm Password</label>
                                        </div>
                                        <div className='col-md-12 mt-2'>
                                            <input type="password" name="confirmPassword" onChange={this.handleChange} value={this.state.confirmPassword} placeholder='confirm  Password' required/>
                                        </div>
                                    </div>
                                    <div className='row mt-5 mb-3 '>
                                        <div className='col-md-12 '>
                                            <button className='login-submit' type="submit" onClick={this.handleChangePassword}>Change Password</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div> 
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps=(state)=>{
    console.log(state)
    return {
        Password_change: state.login.change_password

    }
     
}

const mapDispatchToProps=(dispatch)=>{
    return bindActionCreators({
        loginApi ,
        changePassword,
       },dispatch);
}


// export default Login;
export default connect(mapStateToProps,mapDispatchToProps)(ChangePass);
