import React, { Component } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import durationIcon from '../../../Assests/icon2/term (1).svg'
import chartIcon from '../../../Assests/icon/Component 91.svg'
import { BsCircleFill } from 'react-icons/bs'
import { AiFillStepBackward, AiFillStepForward } from 'react-icons/ai'
import { MisPaginationBar } from '../../../Components/MisPagination'
import { getAllUsersDetails, updateStatus } from '../../../store/Admin/action'
import { Button } from 'semantic-ui-react'
import { Chart as ChartJS, Tooltip, Title, ArcElement, Legend } from 'chart.js'
import { Doughnut } from 'react-chartjs-2'
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import { treemap } from 'd3'
import { data } from 'jquery'
const MySwal = withReactContent(Swal)

ChartJS.register(Tooltip, Title, ArcElement, Legend)
// import { Chart, ChartLegend, ChartSeries, ChartSeriesItem, ChartSeriesLabels } from '@progress/kendo-react-charts';
// import 'hammerjs';
// import data from './power-distribution-data.json';

// const labelContent = e => e.category;
// import { Doughnut } from 'react-chartjs-2';

class User extends Component {
  constructor(props) {
    super(props)
    this.state = {
      userPerPage: 5,
      userDisplayed: [],
      switch_allDocs: true,
      switch_mismatched: false,
      switch_blockedUser: false,
      filteredValue: '',
      start: 0,
      // end:5,

      pieData: {
        datasets: [
          {

            data:[this.props.getAllUsers_list.length , 0,  2],
            backgroundColor: [
              'rgba(191, 221, 166, .8)',
              'rgba(73, 114, 149,.8)',
              'rgba(246, 141, 136,.8)',
            ],
          },
        ],

        // labels:[
        //     'Total Users',
        //     'Blocked Users',
        //     'Pending Approval'
        // ]
      },
      dummydata: [],
      data: [
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Verified Successfully', status:'Verified'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Verified Successfully', status:'Verified'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Verified Successfully', status:'Verified'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Seller Name In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Purchaser Name In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Survey Number In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Verified Successfully', status:'Verified'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Verified Successfully', status:'Verified'}
        // ],
        // missingDocument:[
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'},
        //     {doumentId:'R/Adayar/Book1/1607/2021', remarks:'Document Missing', status:'Missing'}
        // ],
        // blockedUser:[
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Seller Name In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Purchaser Name In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Survey Number In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Extend In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Survey Number In Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Plot Number in Document Index Mismatched', status:'Mismatched'},
        //     {doumentId:'R/Kodambakkam/Book1/1607/2021', remarks:'Purchaser Name In Document Index Mismatched', status:'Mismatched'}
      ],

      blockData: [],
    }
  }

  componentDidMount() {
    this.props.getAllUsersDetails()
    console.log('mount', this.props.getAllUsers_list.length)
  }

  componentDidUpdate(prev) {
    if (prev.getAllUsers_list != this.props.getAllUsers_list) {
      if (this.props.getAllUsers_list.length != 0) {
        let temp = this.state
        temp.data=[];
        for (var i = 0; i < this.props.getAllUsers_list.length; i++) {
          var data = {
            userId: this.props.getAllUsers_list[i]._id,
            email: this.props.getAllUsers_list[i].email,
            role: this.props.getAllUsers_list[i].role,
            status: this.props.getAllUsers_list[i].status,
          }

          temp.data.push(data)
        }
        temp.blockData = temp.data.filter((user) => user.status !== 'Active')

        temp.dummydata = temp.data
        temp.userDisplayed = temp.data.slice(0, temp.userPerPage)
        console.log('hbjhbbbbbbbbb--- ',temp,this.props.getAllUsers_list)
        this.setState(temp)
      }
    }

    if(prev.all_users_action_update!=this.props.all_users_action_update){
      if(this.props.all_users_action_update.message=="Profile updated successfully")
        {
          console.log("test running...")
          this.props.getAllUsersDetails();
        }
    }
  }

  handleSwitchTabs = (name) => {
    if (name == 'switch_allDocs') {
      let data = []

      this.state.filteredValue === ''
        ? (data = this.state.data)
        : (data = this.state.data.filter((user) => {
            return user.role === this.state.filteredValue
          }))

      this.setState({
        ...this.state,
        switch_allDocs: true,
        switch_mismatched: false,
        switch_blockedUser: false,
        dummydata: data,
        userDisplayed: data.slice(0, this.state.userPerPage),
      })
    } else if (name == 'switch_blockedUser') {
      let data = []

      this.state.filteredValue === ''
        ? (data = this.state.data.filter((user) => user.status !== 'Active'))
        : (data = this.state.data.filter((user) => {
            return (
              user.status !== 'Active' && user.role === this.state.filteredValue
            )
          }))

      this.setState({
        ...this.state,
        switch_allDocs: false,
        switch_mismatched: false,
        switch_blockedUser: true,
        dummydata: data,
        userDisplayed: data.slice(0, this.state.userPerPage),
      })
    }
  }

  handleRoute(user) {
    // this.props.history.push(`/batch-id/${id}`)
    this.props.history.push({
      pathname: `/admin/user/userdetails`,
      state: {
        user,
      },
    })
  }

  handleBlock = (id, status) => {
    //add alert
    let action = status === 'Active' ? 'Block' : 'Unblock'

    MySwal.fire({
      title: `Are you sure you want to ${action} the User?`,
      showDenyButton: true,
      confirmButtonText: 'Yes',
      denyButtonText: 'No',
    }).then((result) => {
      if (result.isConfirmed) {
        let update = this.props.updateStatus(id, status)
        if (update) {
          this.componentDidUpdate()
        }
      } else {
        MySwal.fire('Action Cancelled!')
      }
    })
    // console.log(id,' ',status)

    // this.props.blockData
  }
  handlePaginate = (currentPage) => {
    const indexOfLastData = currentPage * this.state.userPerPage
    const indexOfFirstData = indexOfLastData - this.state.userPerPage
    const displayData = this.state.dummydata.slice(
      indexOfFirstData,
      indexOfLastData,
    )
    this.setState({
      ...this.state,
      userDisplayed: displayData,
    })

    console.log('curretn Page ', currentPage)
  }

  filterUser = () => {
    const index = document.getElementById('filterByRoles')
    const filterUserBy = index.options[index.selectedIndex].value

    if (filterUserBy !== 'Select User Type') {
      const dummydata = this.state.data.filter((user) => {
        if (this.state.switch_blockedUser) {
          return user.role === filterUserBy && user.status === 'Inactive'
        } else {
          return user.role === filterUserBy
        }
      })
      console.log('filtered user', dummydata, '', filterUserBy)

      this.setState({
        ...this.state,
        dummydata: dummydata,
        userDisplayed: dummydata.splice(0, this.state.userPerPage),
        filteredValue: filterUserBy,
      })
    }
  }

  render() {
    return (
      <div className="row">
        <div className="col-md-12">
          <div className="igr-batch-id-index">
            <div className="igr-batch-id-Index-layout">
              <div className="row">
                <div className="col-md-12 igr-batch-id-Idnex-header">
                  <img src={durationIcon} /> &nbsp; &nbsp;
                  <span>Users: {this.props.match.params.id}</span>
                </div>
                <div className="col-md-12 mb-4">
                  <div className="row">
                    <div className="col-md-7">
                      <div class="grid-container-element">
                        <div
                          class="grid-child-element purple"
                          style={{ color: 'green' }}
                        >
                          All Users
                        </div>
                        <div class="grid-child-element green">
                          <select
                            value={this.state.filteredValue}
                            id="filterByRoles"
                            className="dropdown  "
                            style={{
                              color: 'green',
                              border: '1px solid #377D22',
                              outline: 'none',
                              padding: '.5rem .7rem',
                            }}
                            onChange={() => {
                              this.filterUser()
                            }}
                          >
                            <option>Select User Type</option>
                            <option value="DIG">DIG</option>
                            <option value="DRO">DRO</option>
                            <option value="SRO">SRO</option>
                          </select>
                        </div>
                      </div>
                      <div className="row m-0">
                        <div className="col-md-4 p-0">
                          <div className="batchId-info-row">
                            <div>
                              <label className="batch-id-row1-title">
                                Total Users
                              </label>
                            </div>
                            <div className="batch-id-row1-data">
                              <div>
                                <label>
                                  {this.props.getAllUsers_list.length}
                                </label>
                              </div>
                            </div>
                          </div>
                        </div>
                        <div className="col-md-4 p-0">
                          <div className="batchId-info-row">
                            <div>
                              <label className="batch-id-row1-title">
                                Blocked Users
                              </label>
                            </div>
                            <div className="batch-id-row2-data">
                              <div>
                                <label> {this.state.blockData.length}</label>
                              </div>
                            </div>
                          </div>
                        </div>
                        <div className="col-md-4 p-0">
                          <div className="batchId-info-row">
                            <div>
                              <label className="batch-id-row1-title">
                                Pending Approval
                              </label>
                            </div>
                            <div className="batch-id-row3-data">
                              <div>
                                <label>0</label>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="col-md-5">
                      <div
                        className="card mt-3"
                        style={{
                          height: '95%',
                          boxShadow: '0px 3px 6px #00000029',
                        }}
                      >
                        <div className="row">
                          <div className="col-md-12 p-0 text-center">
                            <span
                              style={{
                                color: '#000000',
                                fontSize: '.8rem',
                                fontWeight: '600',
                              }}
                            >
                              All Users
                            </span>
                          </div>
                          <div className="col-md-1"></div>
                          <div
                            className="col-md-5 p-0 pb-2"
                            style={{
                              height: 'calc(100% - .7rem)',
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                            }}
                          >
                            <Doughnut
                              data={this.state.pieData}
                              options={{
                                responsive: true,
                                maintainAspectRatio: true,
                                legend: {
                                  display: false,
                                },
                              }}
                            ></Doughnut>
                            {/* <Chart>
                                                        <ChartSeries>
                                                        <ChartSeriesItem type="donut" data={[ {
                                                                    "kind": "Hydroelectric", "share": 0.175
                                                                }, {
                                                                    "kind": "Nuclear", "share": 0.238
                                                                }, {
