import React, { Component } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'

import logo from '../../../Assests/icon2/Bimg.svg'
import rupeeLogo from '../../../Assests/icon2/rupeeimg.svg'
import dailyUploadReport from '../../../Assests/icon2/term (1).svg'
import dataIntegrityReport from '../../../Assests/icon2/Group 481.svg'
import pauseCurrentBatch from '../../../Assests/icon2/Group 482.svg'
import { SimpleModalPop } from '../../../Components/SimpleModalPop'
import {PageLoader} from '../../../Components/PageLoader'
import $ from 'jquery'
import * as bootstrap from 'bootstrap'
import {
  getallMasterApi,
  getZones,
  getDistrict,
  getSROList,
} from '../../../store/action'
import { Chart } from 'chart.js'
import doughchart from '../../../Assests/icon2/doughnuticon.PNG'

class BlockchainDocOfRegistration extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ModalShow: null,
      isBtnClicked: true,
      zone: '',
      zoneArr: [this.props.match.params.role],
      district: '',
      districtArr: [],
      sub_reg_ofc: '',
      sub_reg_ofcArr: [],
      closePop: true,
      isLoading: true,
    }
    console.log('Testing101', this.props.location.state)
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick() {
    this.setState((state) => ({
      isBtnClicked: !state.isBtnClicked,
    }))
  }
  //  handleSubmit=(e)=>{
  //         e.preventDefault();
  //         this.handleAllData();

  // //     }

  //    handleAllData=()=>{
  //         const data={
  //             zone:'',
  //             dist:'',
  //             sro_offc:'',
  //             book:'',
  //             doc_id:'',
  //             year:''
  //             //yet to be done confusion in path either Login or Admin

  //         }
  //     this.props.getallMasterData()
  //     }

  handleChange = ({ target }) => {
    this.setState({
      selectedOption: target.zone,
      selectedOption: target.dist,
      selectedOption: target.sro,
    })
  }
  componentDidMount() {


    this.props.getZones()
    this.props.getDistrict(this.props.match.params.role)
  }

  componentDidUpdate(prev) {
    //     // console.log('Click',this.props.getalld)
    //     if(prev.getalld !=  this.props.getalld){
    //         console.log('Click',this.props.getalld)
    //         if(this.props.getalld.length!=0){
    //             let temp = this.state;

    //     //    for(var i=0; i<this.props.getalld.length; i++){
    //     //             var data={
    //     //                 zone:this.props.getalld.user[i].Zone,
    //     //                  district:this.props.getalld.user[i].District,
    //     //                 d_Id:this.props.getalld.user[i].District_ID,
    //     //                 sro_Id:this.props.getalld[i].user[i].SRO_ID,
    //     //                 // s_Name:this.props.getalld[i].SRO Name ,

    //     //             }
    //     //              console.log("HI Prakash",temp.data);
    //     //             temp.data.push(data);

    //     //         }

    //             this.setState(temp);
    //         }
    //     }
    if (prev.getUrZone != this.props.getUrZone) {
      console.log('Click', this.props.getUrZone)
      if (this.props.getUrZone.length != 0) {
        let temp = this.state

        // for (var i = 0; i < this.props.getUrZone.length; i++) {}

        // zone:this.props.getUrZone.data[i].Zone,
        //  district:this.props.getUrZone.data[i].District,
        // d_Id:this.props.getUrZone.data[i].District_ID,
        // sro_Id:this.props.getUrZone[i].data[i].SRO_ID,
        // s_Name:this.props.getdatamaster[i].SRO Name ,
        // zoneArr : this.props.getUrZone[i]

        console.log('tempdata', temp.data)
        // temp.data.push(data);
        temp.zoneArr = this.props.getUrZone.data
        this.setState(temp)
        this.setState({
          isLoading:false,
        })
      }

      console.log('temp', this.state.zone)
    }

    if (prev.getUrDistrict != this.props.getUrDistrict) {
      console.log('districtOne', this.props.getUrDistrict)
      if (this.props.getUrDistrict.data.length != 0) {
        let temp = this.state

        // for (var i = 0; i < this.props.getUrDistrict.length; i++) {
        //     var data = {
        //         // zone:this.props.getUrZone.data[i].Zone,
        //         //  district:this.props.getUrZone.data[i].District,
        //         // d_Id:this.props.getUrZone.data[i].District_ID,
        //         // sro_Id:this.props.getUrZone[i].data[i].SRO_ID,
        //         // s_Name:this.props.getdatamaster[i].SRO Name ,
        //         zone: this.props.getUrDistrict[i]

        //     }

        console.log('districtArr', temp.data)

        temp.districtArr = this.props.getUrDistrict.data
        this.setState(temp)
      }
    }

    if (prev.sroList != this.props.sroList) {
      if (this.props.sroList.data.length != 0)
        this.setState({
          ...this.state,
          sub_reg_ofcArr: this.props.sroList.data,
        })
    }
  }

  handleChange = (e) => {
    const { name, value } = e.target
    console.log(name, value)

    if ((name == 'zone') & (value != '')) {
      this.props.getDistrict(value)
    }
    if ((name == 'district') & (value != '')) {
      this.props.getSROList(value)
    }
    this.setState({ ...this.state.users, [name]: value })
  }

  showModal() {
    const myModal = new bootstrap.Modal(
      document.getElementById('simpleModal'),
      {
        keyboard: false,
      },
    )
    myModal.show()
    $('.modal-backdrop').removeClass('modal-backdrop')
    // setTimeout(()=>myModal.hide(),5000)
  }


  closeModalPop = () =>{
    this.setState({
      closePop: false,
    })
  }
  render() {


    if(this.state.closePop === false){
      this.setState({
        closePop: true,
      })
    }
    return (
      <div>
        {this.state.isLoading === false ?
          <div className="blockChainDoc-index">
            {this.state.isLoading === true &&
              <PageLoader/>
            }
            {this.state.closePop === true &&
              <SimpleModalPop
                propClass="modal-lg modal-dialog-centered"
                iconSvg={dailyUploadReport}
                closeModalPop={this.closeModalPop}
              >
                <div className="b-index-popup">
                  <div className="b-index-body">
                    <div className="row">
                      <div className="col-md-8 p-0">
                        <div className="row mt-3">
                          <div className="col-md-12 b-index-popup-rowData">
                            <div>
                              <label>Batch ID</label>
                            </div>
                            <div>:</div>
                            <div>
                              <span>113297758</span>
                            </div>
                          </div>
                        </div>
                        <div className="row">
                          <div className="col-md-12 b-index-popup-rowData">
                            <div>
                              <label>Batch Started At</label>
                            </div>
                            <div>:</div>
                            <div>
                              <span>15/02/2018 - 12.00.00 AM</span>
                            </div>
                          </div>
                        </div>
                        <div className="row">
                          <div className="col-md-12 b-index-popup-rowData">
                            <div>
                              <label>No. Of . Documents</label>
                            </div>
                            <div>:</div>
                            <div>
                              <span>2,34,234</span>
                            </div>
                          </div>
                        </div>
                        <div className="row">
                          <div className="col-md-12 b-index-popup-rowData">
                            <div>
                              <label>Verified Documents</label>
                            </div>
                            <div>:</div>
                            <div>
                              <span>98,897</span>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-4">
                        <div style={{ width: '90%', marginLeft: '3rem' }}>
                          {' '}
                          <img src={doughchart} style={{ width: '100%' }} />
                        </div>
                        <div className="row mt-2 mb-2">
                          <div className="col-md-12 p-0 d-flex align-items-center">
                            <div
                              style={{
                                background: '#BFDDA6',
                                width: '1.5rem',
                                height: '0.8rem',
                                borderRadius: '5px',
                              }}
                            ></div>{' '}
                            &nbsp;&nbsp;{' '}
                            <span
                              style={{
                                color: 'rgba(0,0,0,0.7)',
                                fontSize: '0.7rem',
                                fontWeight: 'bold',
                              }}
                            >
                              Verified Documents
                            </span>
                          </div>
                          <div className="col-md-12 p-0 d-flex align-items-center">
                            <div
                              style={{
                                background: '#F68D88',
                                width: '1.5rem',
                                height: '0.8rem',
                                borderRadius: '5px',
                              }}
                            ></div>{' '}
                            &nbsp;&nbsp;{' '}
                            <span
                              style={{
                                color: 'rgba(0,0,0,0.7)',
                                fontSize: '0.7rem',
                                fontWeight: 'bold',
                              }}
                            >
                              Non - Verified Documents
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-md-12 p-0">
                        <button
                          className="b-index-popup-btn"
                          onClick={this.handleClick}
                        >
                          {this.state.isBtnClicked ? 'Pause Batch ' : 'Resume Batch'}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </SimpleModalPop>
            }
            <div className="blockchainDoc-index-layout">
              <div className="row">
                <div className="col-md-12 blockchainDoc-index-layout-row1">
                  <img src={logo} /> &nbsp;&nbsp;{' '}
                  <span>Blockchained Documents Of Registration</span>
                </div>
              </div>
              <div className="blockchainDoc-index-layout-row2">
                <div className="row">
                  <div className="col-md-12 mt-2 blockchain-doc-form-title d-flex align-items-center">
                    <img src={rupeeLogo} /> &nbsp;&nbsp;{' '}
                    <span>View a Registration Document</span>
                  </div>
                </div>
                <div className="blockchain-doc-form-body">
                  <div className="row">
                    <div className="col-md-4">
                      <label>Select Zone </label>
                      <select
                        type="text"
                        name="zone"
                        style={{ color: 'black' }}
                        value={this.state.zone}
                        onChange={this.handleChange}
                      >
                        <option selected>
                          Select Zone{console.log(this.state.zone)}
                        </option>
                        {this.state.zoneArr.length != 0 &&
                          this.state.zoneArr.map((id) => (
                            <option id style={{ color: 'black' }}>
                              {' '}
                              {id}
                            </option>
                          ))}
                      </select>
                    </div>
                    <div className="col-md-4">
                      <label selected>Select District</label>
                      <select
                        type="text"
                        name="district"
                        style={{ color: 'black' }}
                        value={this.state.district}
                        onChange={this.handleChange}
                      >
                        <option selected>
                          Select Zone{console.log(this.state.district)}
                        </option>
                        {this.state.districtArr.length != 0 &&
                          this.state.districtArr.map((id) => (
                            <option id style={{ color: 'black' }}>
                              {' '}
                              {id}
                            </option>
                          ))}
                      </select>
                    </div>
                    <div className="col-md-4">
                      <label>Select Sub Registrar Office</label>
                      <select
                        style={{ color: 'black' }}
                        value={this.state.selectedOption}
                        onChange={this.handleChange}
                      >
                        <option selected>
                          Select SRO Officer{this.state.selectedOption}
                        </option>

                        {this.state.sub_reg_ofcArr != 0 &&
                          this.state.sub_reg_ofcArr.map((id) => (
                            <option id>{id}</option>
                          ))}

                        {console.log(this.state)}
                      </select>
                    </div>
                  </div>
                  <div className="row mt-4">
                    <div className="col-md-4">
                      <label>Choose Book</label>
                      <select>
                        <option className="defaultSelect" selected>
                          Choose Book
                        </option>
                      </select>
                    </div>
                    <div className="col-md-4">
                      <label>Enter Document ID</label>
                      <input type="text" placeholder="Enter Document ID"></input>
                    </div>
                    <div className="col-md-4">
                      <label>Enter Year</label>
                      <select>
                        <option selected>Enter Year</option>
                      </select>
                    </div>
                  </div>
                  <div className="row mb-4">
                    <div className="col-md-12">
                      <button
                        onClick={() =>
                          this.props.history.push('/3-step-document-verification')
                        }
                        className="blockchain-doc-form-button"
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <div className="blockchainDoc-index-layout-row3">
                <div
                  onClick={() => this.props.history.push('/igr-batch-verification')}
                >
                  <img src={dataIntegrityReport} /> &nbsp;&nbsp;&nbsp;{' '}
                  <span>Document Integrity </span>
                </div>
                <div
                  onClick={() => this.props.history.push('/igr-batch-upload')}
                  className="pushRight"
                >
                  {' '}
                  <img src={dailyUploadReport} /> &nbsp;&nbsp;&nbsp;{' '}
                  <span>Daily Upload Report</span>
                </div>
                <div className="pushRight" onClick={this.showModal}>
                  <img src={pauseCurrentBatch} /> &nbsp;&nbsp;&nbsp;{' '}
                  <span>Pause Current Batch</span>
                </div>
              </div>
            </div>
          </div>
        :
        <PageLoader/>
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log('hello', state)
  return {
    // api integration to be done

    getUrZone: state.admin.getallZones,
    getUrDistrict: state.admin.getallDistrict,
    sroList: state.admin.getallSROList,
  }
}

const mapDispatchToProps = (disptach) => {
  return bindActionCreators(
    {
      getallMasterApi,

      getZones,
      getDistrict,
      getSROList,
    },
    disptach,
  )
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(BlockchainDocOfRegistration)
