                                        </div>
                                    </div>

                                    <div className="row ">
                                        <div className='col-md-5'>
                                            {/* <label>Choose Book</label> */}

                                            <input type="text" name="mobile" style={{ width: '80%', borderRadius: '5px', backgroundColor: '#F7FAFC',color:'Black' }} onChange={this.handleChange} value={this.state.mobile} placeholder='Enter Your Mobile Number' />


                                        </div>
                                        <div className='col-md-5'>
                                            {this.state.role !== "DIG" ?
                                                <select type="text" name="district" 
                                                style={{ width: '80%', borderRadius: '5px', backgroundColor: '#F7FAFC',color:'black' }} 
                                                value={this.state.district} 
