                                        <div className='col-md-5'>
                                            <label style={{ marginBottom: '10px' }}>Select Sub-Registrar Office</label>
                                            {/* <input type="text"  style={{width:'80%',borderRadius:'5px'}}  placeholder='Choose Year'></input> */}

                                        </div>
                                    </div>

                                    <div className="row ">
                                        <div className='col-md-5'>
                                            {/* <label>Choose Book</label> */}

